import React from 'react';
import { motion } from 'framer-motion';

const FadeIn = ({ children, delay = 0 }) => (
  <motion.div
    initial={{ opacity: 0, y: 12 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.6, delay }}
  >
    {children}
  </motion.div>
);

export default function YakushkinPortfolio() {
  return (
    <div style={{
      minHeight: '100vh',
      backgroundImage: 'linear-gradient(135deg, #fdf6e3, #f0f9ff, #e6f7f1)',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      color: '#1e293b',
      display: 'flex',
      flexDirection: 'column'
    }}>
      {/* Hero */}
      <section style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', flexGrow: 1, padding: '4rem 2rem' }}>
        <FadeIn>
          <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>Якушкин Д.О.</h1>
        </FadeIn>
        <FadeIn delay={0.1}>
          <p style={{ fontSize: '1.25rem', maxWidth: '600px', textAlign: 'center' }}>
            Индивидуальный предприниматель в строительстве. Команда опытных мастеров, ориентированных на результат.
          </p>
        </FadeIn>
      </section>

      {/* About */}
      <section style={{ maxWidth: '800px', margin: '0 auto', padding: '2rem' }}>
        <FadeIn>
          <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>О нас</h2>
          <p>
            Мы — слаженная бригада специалистов, выполняющая строительные и ремонтные работы любой сложности. На нашем счету множество реализованных объектов — от загородных домов до коммерческих помещений. Работаем ответственно и слаженно, ценим доверие и профессионализм.
          </p>
        </FadeIn>
      </section>

      {/* Contact */}
      <section style={{ maxWidth: '600px', margin: '0 auto', padding: '2rem' }}>
        <FadeIn>
          <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>Связаться</h2>
          <p style={{ marginBottom: '1rem' }}>Позвоните или напишите нам, чтобы обсудить ваш проект.</p>
          <p>📞 +7 953 447-12-18</p>
          <p>📧 dimayakyshkin6@mail.ru</p>
        </FadeIn>
      </section>

      {/* Footer */}
      <footer style={{ textAlign: 'center', padding: '2rem', fontSize: '0.875rem', color: '#64748b' }}>
        © {new Date().getFullYear()} Якушкин Д.О. Все права защищены.
      </footer>
    </div>
  );
}
