import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const FadeIn = ({ children, delay = 0 }) => (
  <motion.div
    initial={{ opacity: 0, y: 12 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.6, delay }}
  >
    {children}
  </motion.div>
);

export default function YakushkinPortfolio() {
  const images = [
    "/images/photo_2025-08-22_20-29-50.webp",
    "/images/photo_2025-08-22_20-30-08.webp",
    "/images/photo_2025-08-22_20-30-18.webp",
    "/images/photo_2025-08-22_20-30-23.webp",
  ]; 

  const [index, setIndex] = useState(0);

  // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src.replace(".jpg", ".webp"); // —Å–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–±—É–µ–º webp
      img.onerror = () => {
        const fallback = new Image();
        fallback.src = src; // –µ—Å–ª–∏ –Ω–µ—Ç webp ‚Üí –≥—Ä—É–∑–∏–º jpg
      };
    });
  }, []);

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∫–∞—Ä—Ç–∏–Ω–æ–∫
  useEffect(() => {
    const interval = setInterval(
      () => setIndex((prev) => (prev + 1) % images.length),
      5000
    );
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div
      style={{
        minHeight: "100vh",
        color: "#1e293b",
        display: "flex",
        flexDirection: "column",
        position: "relative",
        overflow: "hidden",
      }}
    >
      {/* –§–æ–Ω–æ–≤—ã–π —Å–ª–∞–π–¥–µ—Ä */}
      <AnimatePresence>
        <motion.div
          key={index}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 1 }}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url(${images[index]})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            zIndex: -1,
          }}
        />
      </AnimatePresence>

      {/* Hero */}
      <section
        style={{
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          padding: "4rem 2rem",
          color: "#fff",
          textAlign: "center",
        }}
      >
        <FadeIn>
          <h1
            style={{
              fontSize: "3rem",
              marginBottom: "0.5rem",
              letterSpacing: "2px",
              textTransform: "uppercase",
            }}
          >
            REMKOMANDA
          </h1>
        </FadeIn>

        <FadeIn delay={0.1}>
          <h2
            style={{
              fontSize: "1.5rem",
              marginBottom: "1rem",
              fontWeight: "400",
            }}
          >
            –Ø–∫—É—à–∫–∏–Ω –î.–û.
          </h2>
        </FadeIn>

        <FadeIn delay={0.2}>
          <p style={{ fontSize: "1.25rem", maxWidth: "600px", margin: "0 auto" }}>
            –ü—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω—ã–µ —Ä–∞–±–æ—Ç—ã –ø–æ –æ—Ç–¥–µ–ª–∫–µ –∏ –¥–µ–º–æ–Ω—Ç–∞–∂—É –ø–æ–º–µ—â–µ–Ω–∏–π
          </p>
        </FadeIn>
      </section>

      {/* About */}
      <section
        style={{
          maxWidth: "800px",
          margin: "0 auto",
          padding: "4rem 2rem",
          backgroundColor: "rgba(255,255,255,0.8)",
          borderRadius: "1rem",
          marginBottom: "2rem",
        }}
      >
        <FadeIn>
          <h2 style={{ fontSize: "2rem", marginBottom: "1rem" }}>–û –Ω–∞—Å</h2>
          <p>
            –ú—ã ‚Äî –∫–æ–º–∞–Ω–¥–∞ –ø—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª–æ–≤, –≤—ã–ø–æ–ª–Ω—è—é—â–∞—è —Ä–∞–±–æ—Ç—ã –ø–æ –æ—Ç–¥–µ–ª–∫–µ –∏ –¥–µ–º–æ–Ω—Ç–∞–∂—É –ª—é–±—ã—Ö –ø–æ–º–µ—â–µ–Ω–∏–π.
            –†–∞–±–æ—Ç–∞–µ–º –±—ã—Å—Ç—Ä–æ, –∫–∞—á–µ—Å—Ç–≤–µ–Ω–Ω–æ –∏ —Å —Å–æ–±–ª—é–¥–µ–Ω–∏–µ–º –≤—Å–µ—Ö —Å—Ç–∞–Ω–¥–∞—Ä—Ç–æ–≤ –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏.
          </p>
        </FadeIn>
      </section>

      {/* Contact */}
      <section
        style={{
          maxWidth: "600px",
          margin: "0 auto",
          padding: "4rem 2rem",
          backgroundColor: "rgba(0,0,0,0.6)",
          borderRadius: "1rem",
          color: "#fff",
          textAlign: "center",
          marginBottom: "2rem",
        }}
      >
        <FadeIn>
          <h2 style={{ fontSize: "2rem", marginBottom: "1rem" }}>–°–≤—è–∑–∞—Ç—å—Å—è</h2>
          <p style={{ marginBottom: "1rem" }}>
            –ü–æ–∑–≤–æ–Ω–∏—Ç–µ –∏–ª–∏ –Ω–∞–ø–∏—à–∏—Ç–µ –Ω–∞–º, —á—Ç–æ–±—ã –æ–±—Å—É–¥–∏—Ç—å –≤–∞—à –ø—Ä–æ–µ–∫—Ç.
          </p>
          <p>üìû +7 953 447-12-18</p>
          <p>üìß dimayakyshkin6@mail.ru</p>
        </FadeIn>
      </section>

      {/* Footer */}
      <footer
        style={{
          textAlign: "center",
          padding: "2rem",
          fontSize: "0.875rem",
          color: "#fff",
          backgroundColor: "rgba(0,0,0,0.5)",
        }}
      >
        ¬© {new Date().getFullYear()} REMKOMANDA. –ò–ü –Ø–∫—É—à–∫–∏–Ω –î–º–∏—Ç—Ä–∏–π –û–ª–µ–≥–æ–≤–∏—á. –í—Å–µ –ø—Ä–∞–≤–∞ –∑–∞—â–∏—â–µ–Ω—ã.
      </footer>
    </div>
  );
}
